// src/pages/Home.jsx
import { useState } from "react";

function Home() {
  const [tasks, setTasks] = useState([]);
  const [input, setInput] = useState("");

  const addTask = () => {
    if (input.trim()) {
      setTasks([
        ...tasks,
        { text: input, completed: false, completedAt: null },
      ]);
      setInput("");
    }
  };

  const toggleComplete = (index) => {
    const newTasks = [...tasks];
    if (!newTasks[index].completed) {
      newTasks[index].completed = true;
      newTasks[index].completedAt = new Date().toLocaleString();
    } else {
      newTasks[index].completed = false;
      newTasks[index].completedAt = null;
    }
    setTasks(newTasks);
  };

  const removeTask = (index) => {
    const newTasks = [...tasks];
    newTasks.splice(index, 1);
    setTasks(newTasks);
  };

  return (
    <section id="home">
      <h1>Bem-vindo à Lista de Tarefas</h1>

      {/* Campo de input */}
      <div>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Digite sua tarefa"
        />
        <button className="add" onClick={addTask}>
          Adicionar
        </button>
      </div>

      {/* Lista de tarefas */}
      <ul className="task-list">
        {tasks.map((task, index) => (
          <li
            key={index}
            className={task.completed ? "completed" : ""}
          >
            <span>{task.text}</span>

            <div>
              <button
                className={task.completed ? "undo" : "complete"}
                onClick={() => toggleComplete(index)}
              >
                {task.completed ? "↩️" : "✅"}
              </button>

              <button
                className="delete"
                onClick={() => removeTask(index)}
              >
                ❌
              </button>
            </div>

            {/* Data de conclusão */}
            {task.completed && (
              <small style={{ display: "block", marginTop: "6px", color: "#555" }}>
                Concluído em: {task.completedAt}
              </small>
            )}
          </li>
        ))}
      </ul>
    </section>
  );
}

export default Home;
