// src/pages/Home.jsx
import { useState, useEffect } from "react";

function Home() {
  const [tasks, setTasks] = useState([]);
  const [input, setInput] = useState("");

  // Carregar do localStorage ao iniciar
  useEffect(() => {
    const saved = localStorage.getItem("tasks");
    if (saved) setTasks(JSON.parse(saved));
  }, []);

  // Salvar no localStorage sempre que tasks mudar
  useEffect(() => {
    localStorage.setItem("tasks", JSON.stringify(tasks));
  }, [tasks]);

  const addTask = () => {
    if (input.trim()) {
      const newTask = { text: input, completed: false, date: null };
      setTasks([...tasks, newTask]);
      setInput("");
    }
  };

  const completeTask = (index) => {
    const newTasks = [...tasks];
    newTasks[index].completed = true;
    newTasks[index].date = new Date().toLocaleString();
    setTasks(newTasks);
  };

  const undoTask = (index) => {
    const newTasks = [...tasks];
    newTasks[index].completed = false;
    newTasks[index].date = null;
    setTasks(newTasks);
  };

  const removeTask = (index) => {
    const newTasks = [...tasks];
    newTasks.splice(index, 1);
    setTasks(newTasks);
  };

  return (
    <section id="home">
      <h1>Bem-vindo à Lista de Tarefas</h1>
      <div>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Digite sua tarefa"
        />
        <button className="add" onClick={addTask}>Adicionar</button>
      </div>

      <ul className="task-list">
        {tasks.map((task, index) => (
          <li key={index} className={task.completed ? "completed" : ""}>
            <span>{task.text} {task.completed && <em>({task.date})</em>}</span>
            {!task.completed && (
              <button className="complete" onClick={() => completeTask(index)}>✔️</button>
            )}
            {task.completed && (
              <button className="undo" onClick={() => undoTask(index)}>↩️</button>
            )}
            <button className="delete" onClick={() => removeTask(index)}>❌</button>
          </li>
        ))}
      </ul>
    </section>
  );
}

export default Home;
