// src/App.jsx
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import Home from "./pages/Home";
import About from "./pages/About";
import Completed from "./pages/Completed"; // ⬅️ importar nova página
import Footer from "./components/Footer";

function App() {
  return (
    <Router>
      <header>
        <nav>
          <Link to="/">Home</Link> |{" "}
          <Link to="/about">Sobre</Link> |{" "}
          <Link to="/completed">Concluídas</Link> {/* ⬅️ novo menu */}
        </nav>
      </header>

      <main>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/about" element={<About />} />
          <Route path="/completed" element={<Completed />} /> {/* ⬅️ nova rota */}
        </Routes>
      </main>

      <Footer />
    </Router>
  );
}

export default App;
