// src/pages/Home.jsx
import { useState, useEffect } from "react";

function Home() {
  const [tasks, setTasks] = useState([]);
  const [input, setInput] = useState("");

  // Carrega tarefas do localStorage ao iniciar
  useEffect(() => {
    const storedTasks = localStorage.getItem("tasks");
    if (storedTasks) {
      setTasks(JSON.parse(storedTasks));
    }
  }, []);

  // Salva tarefas no localStorage sempre que mudam
  useEffect(() => {
    localStorage.setItem("tasks", JSON.stringify(tasks));
  }, [tasks]);

  const addTask = () => {
    if (input.trim()) {
      setTasks([...tasks, { text: input, completed: false, date: null }]);
      setInput("");
    }
  };

  const toggleComplete = (index) => {
    const newTasks = [...tasks];
    newTasks[index].completed = !newTasks[index].completed;
    newTasks[index].date = newTasks[index].completed ? new Date().toLocaleString() : null;
    setTasks(newTasks);
  };

  const removeTask = (index) => {
    const newTasks = [...tasks];
    newTasks.splice(index, 1);
    setTasks(newTasks);
  };

  return (
    <section id="home">
      <h1>Bem-vindo à Lista de Tarefas</h1>
      <div>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Digite sua tarefa"
        />
        <button onClick={addTask} className="add">Adicionar</button>
      </div>
      <ul className="task-list">
        {tasks.map((task, index) => (
          <li key={index} className={task.completed ? "completed" : ""}>
            <span onClick={() => toggleComplete(index)}>
              {task.text} {task.completed && <em>(Concluída em {task.date})</em>}
            </span>
            <button onClick={() => removeTask(index)} className="delete">❌</button>
          </li>
        ))}
      </ul>
    </section>
  );
}

export default Home;
